$(function () {
    // kill 参数数组
    var killConfigArray = void 0;

    // 克隆input 到 tempDOM
    var cloneDOM = function cloneDOM($tempDOM, DOM) {
        $tempDOM.html(DOM);
    };

    // attr
    // let attr = '';
    // let $attr = $('#attr');
    // let $attrText = $attr.find('.attr');
    // let $nameAttrArray = $attr.find('[name="attr"]');

    // 是否折行 （‘\n’）
    var $format = $('#format');
    var $formatInput = $format.find('input');
    // let wrap = ''; // 起始选中连续
    var wrap = '';
    $formatInput.eq(0).on('click', function (e) {
        wrap = '\n';
    });
    $formatInput.eq(1).on('click', function (e) {
        wrap = '';
    });

    // 控制 checked 现实状态
    if (wrap === '\n') {
        $formatInput.eq(0).attr('checked', true);
        $formatInput.eq(1).attr('checked', false);
    } else {
        $formatInput.eq(0).attr('checked', false);
        $formatInput.eq(1).attr('checked', true);
    }

    // 判断是否有 class
    var classHandler = function classHandler($tag) {
        var className = $tag.attr('class');
        return className && className.length > 0 ? ' class="' + className + '"' : '';
    };

    // 主处理器 killHandler
    var killHandler = function killHandler($tempDOM, i) {

        console.log($tempDOM[0], i);

        // 声明 killAttr属性 array:([0]:中文名, [1]:nodeid, [2]:attr);
        var killAttrs = killConfigArray[i] ? killConfigArray[i] : ['未命名栏目', '888888', '默认'];

        console.log('killAttrs:', killAttrs);

        // 预处理 tempDOM
        var $a = $tempDOM.find('a');
        var $abs = $tempDOM.find('.abs');
        var $subTitle = $tempDOM.find('.subTitle');
        var $img = $tempDOM.find('img');
        var imgWidth = $img.attr('width');
        var imgHeight = $img.attr('height');

        // 声明 nodeid,attr, repeat
        var nodeid = killAttrs[1];
        var attr = killAttrs[2];

        var repeat = 0;
        // (() => {
        //     let $div = $tempDOM.find('div');
        //     if ($div) {
        //         let divSize = $div.size();
        //         if (divSize > 0) {
        //             return divSize;
        //         }
        //     }
        // })();
        var dayspan = repeat < 200 || dayspan === undefined ? 0 : repeat;

        // cms 规则
        var cms = {
            lt: '&lt;',
            gt: '&gt;',
            enpquot: '#enpquot#',
            Picture: {
                needcode: ['<img data-src="<Picture needcode=0>PictureUrlPh</Picture>">', '<img data-original="<Picture needcode=0>PictureUrlPh</Picture>">', '<Picture needcode=1 width=' + (imgWidth ? imgWidth : 100) + ' height=' + (imgHeight ? imgHeight : 100) + '>PictureUrlPh</Picture>']
            },
            Title: '<Title length="0">TitlePh</Title>',
            ArticleUrlPh: 'ArticleUrlPh',
            Abstract: '<Abstract>AbstractPh</Abstract>',
            Subtitle: '<Subtitle>SubtitlePh</Subtitle>'
        };

        // 处理 a 内部内容
        // let aHandler = () => {
        $a.each(function (i, e) {
            var $this = $(e);
            // console.log('$a0:', $this.find('img').size() === 0);
            // console.log('$a1:', $this.text().indexOf('详细') !== 0);
            if ($this.find('img').size() === 0 && $this.text().indexOf('详细') !== 0) {
                $this.text(cms.Title);
            }
            // else {
            //     let html = $.trim($(e).html());
            //     console.log(html);
            //     $this.html(html);
            // }
        });

        // 处理 $abs
        $abs.each(function (i, e) {
            var $this = $(e);
            // console.log('$abs0:', $this.find('a').size() > 0);
            if ($this.find('a').size() > 0) {
                var aText = $this.find('a').text();
                console.log('$abs1:\n', aText);
                // $this.text(cms.Abstract + 'URL_BEGIN<a href="' + cms.ArticleUrlPh + '">URL_END' + aText + '</a>');
                $this.text(cms.Abstract + 'URL_BEGIN<a href="' + cms.ArticleUrlPh + '">URL_END' + aText + '</a>');
            } else {
                $this.text(cms.Abstract);
            }
        });

        // 处理 $subTitle
        $subTitle.each(function (i, e) {
            var $this = $(e);
            if ($this.find('a').size() > 0) {
                $this.text('URL_BEGIN<a href="' + cms.ArticleUrlPh + '">URL_END' + cms.Subtitle + '</a>');
            } else {
                $this.text(cms.Subtitle);
            }
        });

        // 处理 a href自身
        $a.attr('href', cms.ArticleUrlPh).before('URL_BEGIN').prepend('URL_END');

        // 处理 img
        $img.each(function (i, e) {
            var $this = $(e);
            $this.replaceWith(function () {
                var temp = cms.Picture.needcode[2];
                if ($this.attr('data-src')) {
                    temp = cms.Picture.needcode[0];
                } else if ($this.attr('data-original')) {
                    temp = cms.Picture.needcode[1];
                }
                return temp;
            });
        });

        // 获取预处理过的 tempDOM
        var orgHtml = $tempDOM.html();

        // 处理 li DOM
        console.log('ul:', $tempDOM.is('ul'));
        console.log('div:', $tempDOM.hasClass('swiper-wrapper'));

        var outputType = 'html';
        var $ul = $tempDOM;

        if ($tempDOM.is('ul')) {

            // ul 处理
            var $li = $ul.children('li');
            var size = $li.size();

            console.log('li DOM1:', size > 1);

            if (size > 1) {
                // 处理 ul 的 class
                var ulClass = classHandler($ul);
                console.log(ulClass);

                // 处理 li 的 class
                var liClass = classHandler($li);
                console.log(liClass);

                // 转换 orgHtml
                orgHtml =
                // `<ul${ulClass} kill="${killAttrs}">
                '<ul' + ulClass + '>\n                        <Repeat Begin=0 End=' + size + '>\n                            <Article>\n                                <li' + liClass + '>' + $li.eq(0).html() + '</li>\n                            </Article>\n                        </Repeat>\n                    </ul>';
                outputType = 'replaceWith';
            }
        } else if ($tempDOM.children('div').size() > 1) {

            // rot swiper-wrapper 处理
            var _$li = $ul.children('div');
            var _size = _$li.size();

            console.log('div DOM1:', _size > 1);

            if (_size > 1) {
                // 处理 ul 的 class
                var _ulClass = classHandler($ul);
                console.log(_ulClass);
                // 处理 li 的 class
                var _liClass = classHandler(_$li);
                console.log(_liClass);
                // 转换 orgHtml
                orgHtml = '<Repeat Begin=0 End=' + _size + '>\n                        <Article>\n                            <div' + _liClass + '>' + _$li.eq(0).html() + '</div>\n                        </Article>\n                    </Repeat>';
            }
        } else {
            // 添加 Article
            orgHtml = '<Article>' + orgHtml + '</Article>';
            // // 添加 Repeat
            // if (repeat !== '' && repeat > 0) {
            //     orgHtml = `<Repeat Begin=0 End=${repeat}>${orgHtml}</Repeat>`;
            // }
        }

        // 格式化 orgHtml
        orgHtml = $.HTMLFormat(orgHtml, {
            indent_size: wrap === '\n' ? 4 : 0,
            wrap: wrap === '\n' ? true : false,
            indent_character: wrap === '\n' ? ' ' : ''
        });

        console.log('HTMLFormat:\n', orgHtml);
        // console.log('orgHtml1:', orgHtml);

        // 使用正则转码
        orgHtml = orgHtml.replace(/picture/g, 'Picture').replace(/URL_BEGIN/g, '<Url>').replace(/URL_END/g, '</Url>').replace(/</g, cms.lt).replace(/>/g, cms.gt).replace(/"/g, cms.enpquot);

        // console.log('newHtml:', orgHtml)
        var attrZh = attr === '' || attr === null || attr === 'null' || attr === '默认' ? '默认' : attr.replace('61', '图片').replace('62', '头条').replace('63', '普通');

        // 重新给 outputTemp 赋值
        var outputTemp = '\n            <!-- ' + killAttrs[0] + ' BEGIN nodeid:' + nodeid + ', attr:' + attrZh + ' -->\n            <!--webbot bot="AdvTitleList" nodeid="' + (nodeid === '' ? '888888' : nodeid) + '" type="0" spanmode="0" dayspan="' + dayspan + '" attr="' + (attr === '' || attr === 'null' || attr === '默认' ? '' : attr) + '" comstring="' + wrap + orgHtml + wrap + '" TAG="BODY" PREVIEW="[高级标题列表]" artattr="0" isshowcode="0" titlekeyword="" keyword="" tagstring="00" starttime="" endtime="" id="" startspan --><!--webbot bot="AdvTitleList" endspan i-checksum="0" -->\n            <!-- ' + killAttrs[0] + ' END -->\n            ';

        // 给kill 添加内容
        // $tempDOM.attr('kill', killAttrs);
        $tempDOM.removeAttr('kill');

        // 输出结果至 kill DOM位置
        if (outputType === 'html') {
            $tempDOM.html(outputTemp);
        } else if (outputType === 'replaceWith') {
            $tempDOM.replaceWith(outputTemp);
        }
    };

    // 提交
    var $submit = $('#submit');
    $submit.on('click', function () {

        var $input = $('#input');
        var inputVal = $input.val();
        var $tempDOM = $('#tempDOM');

        // 隐藏临时DOM
        // $tempDOM.show();

        // 克隆 input 内容转为 killHandler 可以处理的 DOM结构
        cloneDOM($tempDOM, $.trim(inputVal));

        // 获取 kill-cms节点
        var $killCms = $tempDOM.find('[kill]');

        // 重置 killConfigArray
        killConfigArray = [];

        // 获取 killConfig
        var $killConfig = $tempDOM.find('#killConfig');
        var $killConfigLi = $killConfig.find('li');

        $killConfigLi.each(function (i, e) {
            var t = $(e).text();
            t = t.split(',');

            t[0] = $.trim(t[0]); // 栏目名
            t[1] = $.trim(t[1]); // 信息片
            t[2] = $.trim(t[2]); // 属性

            // 转换 attr
            // console.log(t[1].indexOf('默认') >= 0);
            if (t[2].indexOf('默认') >= 0 || t[1] === '') {
                t[2] = 'null';

                // 添加清除重复的中文属性功能
                t[2] = t[2].replace(new RegExp('默认', 'g'), '');
            } else {
                t[2] = t[2].replace('图片', '+61').replace('头条', '+62').replace('普通', '+63');

                // 添加清除重复的中文属性功能
                t[2] = t[2].replace(new RegExp('图片', 'g'), '').replace(new RegExp('头条', 'g'), '').replace(new RegExp('普通', 'g'), '');
            }

            killConfigArray.push(t);
        });
        // let killCmsLen = $killCms.length;
        // let killConfigLiLen = $killConfigLi.length;

        // 移除 tempDOM 的 #killConfig
        $killConfig.remove();

        // killHandler each
        $killCms.each(function (i, e) {
            killHandler($(e), i);
        });

        // 输出结果
        var $output = $('#output');
        $output.text($tempDOM.html());

        // if (killCmsLen <= killConfigLiLen) {
        //
        // } else {
        //     alert(`请检查"killConfig"配置, 我们发现似乎缺少"${killCmsLen - killConfigLiLen}"个项目`);
        // }
    });

    // footer添加版本信息，读取 package.json 中 version对象
    $.when($.ajax('package.json')).then(function (data) {
        var v = data.version;
        $('title').append(' v' + v);
        $('#version').append(' v ' + v);
    });
});
